<script>
  import {
    liveGamepiece,
    gameState,
    gameData,
    autoDataLog,
    teleDataLog,
    liveLocation,
  } from "./stores";

  export let level = 1;
  export let type = "Success";
  export let gameMode = "Auto";

  let dataField = " ";
  let locationField = " ";

  let displayConeValue = 0;
  let displayCubeValue = 0;

  function update() {
    if ($gameState === 0) {
      //            $liveLocation = "Floor"; //force pickup location to be floor in auto
      $autoDataLog.push(JSON.parse(JSON.stringify($gameData)));
    } else {
      $teleDataLog.push(JSON.parse(JSON.stringify($gameData)));
    }
    if (gameMode === "Tele" && ($liveLocation.includes("Spike") || $liveLocation.includes("Centerline"))){
      dataField = "Auto" + gameLevelMap[level] + $liveGamepiece + type;
      $gameData[dataField] = $gameData[dataField] + 1;
      dataField = "TeleFloorNoteCollect";
      $gameData[dataField] = $gameData[dataField] - 1;
      $liveLocation = "Floor"; 
    }
    console.log("LiveGamePiece" + $liveGamepiece);
    console.log("LiveLocation" + $liveLocation);
    console.log("GameMode" + gameMode);
    dataField = gameMode + gameLevelMap[level] + $liveGamepiece + type;
    locationField = gameMode + $liveLocation + $liveGamepiece + "Collect";
    $gameData[dataField] = $gameData[dataField] + 1;
    $gameData[locationField] = $gameData[locationField] + 1;
    $liveGamepiece = 0;
    if ($gameState === 0 && locationField !== "AutoPreLoadedNoteCollect") {
      $gameData["AutoPath"].push(locationField);
    } 
  }

  let gameLevelMap = {
    1: "Amp",
    2: "Speaker",
    3: "Trap",
  };
</script>

